        public TdrError.ErrorType readInt16(ref Int16 dest, Int32 pos)
        {
            if (null == beginPtr)
            {
#if (DEBUG)
                StackTrace st = new StackTrace(true);
                for (int i = 0; i < st.FrameCount; i++)
                {
                    if (null != st.GetFrame(i).GetFileName())
                    {
                        Console.WriteLine(st.GetFrame(i).ToString());
                    }
                }
#endif
                return TdrError.ErrorType.TDR_ERR_ARG_IS_NULL;
            }

            if (sizeof(Int16) > (length - pos))
            {
#if (DEBUG)
                StackTrace st = new StackTrace(true);
                for (int i = 0; i < st.FrameCount; i++)
                {
                    if (null != st.GetFrame(i).GetFileName())
                    {
                        Console.WriteLine(st.GetFrame(i).ToString());
                    }
                }
#endif
                return TdrError.ErrorType.TDR_ERR_SHORT_BUF_FOR_READ;
            }

#if TDR_MANUAL_UNPACK
#if TDR_SAFE_CODE
            if (IsNetEndian && BitConverter.IsLittleEndian)
            {
                dest = (short)((short)(beginPtr[pos] << 8) | ((short)beginPtr[pos + 1]));
            }
            else
            {
                dest = (short)(((short)beginPtr[pos]) | (short)(beginPtr[pos + 1] << 8));
            }
#else
        unsafe
        {
            fixed (byte* pbyte = &beginPtr[pos])
            {
                if (IsNetEndian && BitConverter.IsLittleEndian)
                {
                    dest = (short)((*pbyte << 8) | (*(pbyte + 1)));
                }
                else
                {
                    if (pos % 2 == 0)
                    {
                        dest = *((short*)pbyte);
                    }
                    else
                    {
                        dest = (short)((*pbyte) | (*(pbyte + 1) << 8));
                    }
                }
            }
        }
#endif
#else
        dest = BitConverter.ToInt16(beginPtr,pos);

        if (IsNetEndian && BitConverter.IsLittleEndian)
        {
            dest = IPAddress.NetworkToHostOrder(dest);
        }
#endif
            return TdrError.ErrorType.TDR_NO_ERROR;
        }

