        public TdrError.ErrorType readInt64(ref Int64 dest, Int32 pos)
        {
            if (null == beginPtr)
            {
#if (DEBUG)
                StackTrace st = new StackTrace(true);
                for (int i = 0; i < st.FrameCount; i++)
                {
                    if (null != st.GetFrame(i).GetFileName())
                    {
                        Console.WriteLine(st.GetFrame(i).ToString());
                    }
                }
#endif
                return TdrError.ErrorType.TDR_ERR_ARG_IS_NULL;
            }

            if (sizeof(Int64) > (length - pos))
            {
#if (DEBUG)
                StackTrace st = new StackTrace(true);
                for (int i = 0; i < st.FrameCount; i++)
                {
                    if (null != st.GetFrame(i).GetFileName())
                    {
                        Console.WriteLine(st.GetFrame(i).ToString());
                    }
                }
#endif
                return TdrError.ErrorType.TDR_ERR_SHORT_BUF_FOR_READ;
            }

#if TDR_MANUAL_UNPACK
#if TDR_SAFE_CODE
            if (IsNetEndian && BitConverter.IsLittleEndian)
            {
                dest = ((long)beginPtr[pos] << 56) | ((long)beginPtr[pos + 1] << 48) | ((long)beginPtr[pos + 2] << 40) | ((long)beginPtr[pos + 3] << 32) | ((long)beginPtr[pos + 4] << 24) | ((long)beginPtr[pos + 5] << 16) | ((long)beginPtr[pos + 6] << 8) | ((long)beginPtr[pos + 7]);
            }
            else
            {
                dest = ((long)beginPtr[pos]) | ((long)beginPtr[pos + 1] << 8) | ((long)beginPtr[pos + 2] << 16) | ((long)beginPtr[pos + 3] << 24) | ((long)beginPtr[pos + 4] << 32) | ((long)beginPtr[pos + 5] << 40) | ((long)beginPtr[pos + 6] << 48) | ((long)beginPtr[pos + 7] << 56);
            }
#else
        unsafe
        {
            fixed (byte* pbyte = &beginPtr[pos])
            {
                if (IsNetEndian && BitConverter.IsLittleEndian)
                {
                    int i1 = (*pbyte << 24) | (*(pbyte + 1) << 16) | (*(pbyte + 2) << 8) | (*(pbyte + 3));
                    int i2 = (*(pbyte + 4) << 24) | (*(pbyte + 5) << 16) | (*(pbyte + 6) << 8) | (*(pbyte + 7));
                    dest = (uint)i2 | ((long)i1 << 32);
                }
                else
                {
                    if (pos % 8 == 0)
                    {
                        dest = *((long*)pbyte);
                    }
                    else
                    {
                        int i1 = (*pbyte) | (*(pbyte + 1) << 8) | (*(pbyte + 2) << 16) | (*(pbyte + 3) << 24);
                        int i2 = (*(pbyte + 4)) | (*(pbyte + 5) << 8) | (*(pbyte + 6) << 16) | (*(pbyte + 7) << 24);
                        dest =  (uint)i1 | ((long)i2 << 32);
                    }
                }
            }
        }
#endif
#else
        dest = BitConverter.ToInt64(beginPtr, pos);

        if (IsNetEndian && BitConverter.IsLittleEndian)
        {
            dest = IPAddress.NetworkToHostOrder(dest);
        }
#endif
            return TdrError.ErrorType.TDR_NO_ERROR;
        }

